    card = undefined;
export default {
  props: ["cartGrandTotal", "cartItemTotal", "cartSubTotal", "shippingCost", "cartTotalUSD", 'loader', 'showLoader', 'hideLoader', 'loaderIndex', "coupon_applied", "coupon_code"],
  el: "#selector",
  components: {
    Modal,
    LoadingView
  },

  data() {
    return {
      cart: {
        cartItems: []
      },
      id: "",
      first_name: "",
      last_name: "",
      email: "",
      phone_number: "",
      address: "",
      county: "",
      city: "",
      town: "",
      gender: "",
      errors: [],
      lipaNaMpesa: false,
      paymentConfirm: false,
      checked: false,
      errors: [],
      payment_number: "",
      transaction_id: "",
      isModalVisible: false,
      mpesaRefs: [],
      mpesaRef: "",
      is_paid: false,
      payment_ref: "",
      watcherMsg: [],
      rfStyle: null,
      bWidth: null,
      payment_number2: "",
      pStyle: null,
      p2Style: null,
      isAuthenticated: false,
      deliveryOption: false,
      unknownVal: "",
      showStripe: false,
      coupon_amount: 0,
      placedOrder: [],
      placedOrderID: "",
      placedOrderUser: 0,
      couponArr: [],
      coupon_status: "Inactive",
      new_coupon_status: "Used"
    };
  },

  watch: {
    mpesaRef(value) {
      this.mpesaRef = value;
      this.validatePaymentRef(value);
    },

    payment_number2(value) {
      this.payment_number2 = value;
      this.validatePhoneNumber(value);
    },

    payment_number(value) {
      this.payment_number = value;
      this.validatePhoneNumber2(value);
    }

  },
  computed: {
    setShippingCost() {
      let shipCost = 0;

      if (this.cartSubTotal == 0 || this.deliveryOption == false) {
        this.shipCost = 0;
      } else {
        if (this.town == "Bondo" || this.town == "Siaya" || this.town == "Nairobi" || this.town == "Ugunja") {
          this.shipCost = 100;
          console.log("The shipping cost is ", this.shipCost);
        } else {
          if (this.town == "Busia" || this.town == "Kiambu" || this.town == "Kikuyu" || this.town == "Kisumu" || this.town == "Luanda" || this.town == "Thika") {
            this.shipCost = 200;
          } else {
            if (this.town == "Kakamega") {
              this.shipCost = 250;
            } else {
              if (this.town == "Ahero" || this.town == "Bungoma" || this.town == "Machakos" || this.town == "Mumias" || this.town == "Naivasha" || this.town == "Nakuru") {
                this.shipCost = 300;
              } else {
                if (this.town == "Kericho" || this.town == "Migori" || this.town == "Kitale") {
                  this.shipCost = 400;
                } else {
                  if (this.town == "Eldoret" || this.town == "Embu" || this.town == "Kisii" || this.town == "Nyamira") {
                    this.shipCost = 500;
                  } else {
                    if (this.town == "Malindi" || this.town == "Mombasa" || this.town == "Mtwapa" || this.town == "Ukunda") {
                      this.shipCost = 600;
                    } else {
                      this.shipCost = 0;
                    }
                  }
                }
              }
            }
          }
        }
      }

      return this.shipCost;
    },

    checkoutTotal() {
      return this.cartSubTotal + this.setShippingCost - this.coupon_applied;
    }

  },
  methods: {
    validatePaymentRef(value) {
      if (value.length == "") {
        this.watcherMsg["mpesaRef"] = "";
        this.rfStyle = "";
        this.bWidth = 1;
      } else {
        if (/^[A-Z0-9]$/.test(value) && value.length > 9) {
          this.rfStyle = "green";
          this.watcherMsg["mpesaRef"] = "";
          this.bWidth = 2;
        } else {
          this.rfStyle = "red";
          this.watcherMsg["mpesaRef"] = "Incomplete Reference Number";
          this.bWidth = 2;
        }
      }
    },

    validatePhoneNumber2(value) {
      if (value == "") {
        this.p2Style = "";
        this.watcherMsg["payment_number"] = "";
      } else {
        if (/^(?:254)?((?:(?:7(?:(?:[01249][0-9])|(?:5[789])|(?:6[89])))|(?:1(?:[1][0-5])))[0-9]{6})$/.test(value) || /^(?:254|\+254|0)?((?:(?:7(?:(?:3[0-9])|(?:5[0-6])|(8[5-9])))|(?:1(?:[0][0-2])))[0-9]{6})$/.test(value)) {
          this.p2Style = "green";
          this.watcherMsg["payment_number"] = "";
          this.bWidth = 2;
        } else {
          this.p2Style = "red";
          this.watcherMsg["payment_number"] = "Invalid Phone Number";
          this.bWidth = 2;
        }
      }
    },

    validatePhoneNumber(value) {
      if (value == this.payment_number) {
        this.pStyle = "green";
        this.watcherMsg["payment_number2"] = "";
      } else {
        this.pStyle = "red";
        this.watcherMsg["payment_number2"] = "Invalid Phone Number";
      }
    },

    getProfileDetails() {
      this.axios.get("/api/v1/users/me/").then(response => {
        this.phone_number = response.data.phone_number;
        this.email = response.data.email;
        this.id = response.data.id;
        this.first_name = response.data.first_name;
        this.last_name = response.data.last_name;
        this.gender = response.data.gender;
        this.city = response.data.city;
        this.county = response.data.county;
        this.address = response.data.address;
      }).catch(error => {});
    },

    registerCallbackUrl() {
      this.axios.get("/api/v1/c2b/register").then(response => {
        console.log(response.data);
      }).catch(error => {
        console.log(error);
      });
    },

    async payViaMpesa() {
      this.errors = [];

      if (this.payment_number === "") {
        this.errors.push("Please enter phone number");
        this.$toast.error("Invalid phone number", {
          duration: 3000
        });
      }

      if (!this.errors.length) {
        let formData = {
          payment_number: this.payment_number,
          first_name: this.first_name,
          orderTotal: this.checkoutTotal
        };
        this.showLoader();
        this.axios.post("/api/v1/online/lipa/", formData).then(response => {
          console.log(response.data);
          this.paymentConfirm = true;
        }).catch(error => {
          console.log(error);
        }).finally(() => {
          this.hideLoader();
        });
      }
    },

    getMpesaPayments() {
      this.mpesaRefs = [];
      this.axios.get("api/v1/mpesa-payments/").then(response => {
        for (let i = 0; i < response.data.length; i++) {
          let trans_id = response.data[i].transaction_id;
          this.mpesaRefs.push(trans_id);
        }

        console.log(this.mpesaRefs);
        this.isModalVisible = true;
      }).catch(error => {
        console.log(error);
      });
    },

    confirmPayment() {
      if (this.mpesaRefs.includes(this.mpesaRef)) {
        this.is_paid = true;
        this.payment_ref = this.mpesaRef;
        this.$toast.success("Payment Confirmation Succesful", {
          duration: 3000
        });
        this.placeOrder();
      } else {
        this.$toast.error("Invalid Reference Number", {
          duration: 3000
        });
      }
    },

    payViaBank() {
      // STRIPE SETTINGS
      let style = {
        base: {
          border: "1px solid #D8D8D8",
          borderRadius: "4px",
          color: "#000"
        },
        invalid: {// All of the error styles go inside of here.
        }
      };
      let card = elements.create("card", style);
      card.mount(this.$refs.card);
      this.showStripe = !this.showStripe;
    },

    placeOrder() {
      this.showLoader();
      const items = [];

      for (let i = 0; i < this.cart.cartItems.length; i++) {
        const item = this.cart.cartItems[i];
        const obj = {
          product: item.items.id,
          quantity: item.quantity,
          price: item.items.price * item.quantity
        };
        items.push(obj);
      }

      let formData = {
        first_name: this.first_name,
        last_name: this.last_name,
        address: this.address,
        county: this.county,
        city: this.city,
        phone_number: this.phone_number,
        email: this.email,
        items: items,
        order_total: this.checkoutTotal,
        payment_reference: this.payment_ref,
        paid: this.is_paid,
        delivery_fee: this.setShippingCost,
        coupon_applied: this.coupon_applied
      };
      this.axios.post("/api/v1/checkout/", formData).then(response => {
        this.placedOrder = response.data;
        console.log("The placed order total is ", this.placedOrder.order_total);
        this.$store.commit("clearCart");
        this.$toast.success("Order succesfully placed");
        this.placedOrderID = this.placedOrder.id;
        this.placedOrderUser = this.placedOrder.user;
        this.$router.push("/");
        this.$store.commit("reloadingPage");
        this.$router.push("/");
      }).catch(error => {
        console.log(error);
      }).finally(() => {
        this.axios.get(`/api/v1/coupons-list/${this.coupon_code}/`).then(response => {
          this.couponArr = response.data;
        }).catch(error => {
          console.log(error.message);
        }).finally(() => {
          let formData = {
            coupon_amount: this.couponArr.coupon_amount,
            coupon_order: this.couponArr.coupon_order,
            coupon_user: this.couponArr.coupon_user,
            created_at: this.couponArr.created_at,
            status: this.new_coupon_status
          };
          this.axios.put("/api/v1/coupons-list/" + this.coupon_code + "/", formData).then(response => {
            console.log("The status of the coupon is ", response.data);
          }).catch(error => {
            console.log(error.message);
          }).finally(() => {
            this.hideLoader();
            this.generateOrderCoupon();
          });
        });
      });
    },

    generateOrderCoupon() {
      if (this.checkoutTotal >= 10000 && this.checkoutTotal < 20000) {
        this.coupon_amount = 500;
      } else if (this.checkoutTotal >= 20000 && this.checkoutTotal < 30000) {
        this.coupon_amount = 1000;
      } else if (this.checkoutTotal >= 30000 && this.checkoutTotal < 40000) {
        this.coupon_amount = 1500;
      } else if (this.checkoutTotal >= 40000 && this.checkoutTotal < 50000) {
        this.coupon_amount = 2000;
      } else if (this.checkoutTotal >= 50000 && this.checkoutTotal < 60000) {
        this.coupon_amount = 2500;
      } else if (this.checkoutTotal >= 60000 && this.checkoutTotal < 70000) {
        this.coupon_amount = 3000;
      } else if (this.checkoutTotal >= 70000 && this.checkoutTotal < 80000) {
        this.coupon_amount = 3500;
      } else if (this.checkoutTotal >= 80000 && this.checkoutTotal < 90000) {
        this.coupon_amount = 4000;
      } else if (this.checkoutTotal >= 90000 && this.checkoutTotal < 100000) {
        this.coupon_amount = 4500;
      } else if (this.checkoutTotal >= 100000) {
        this.coupon_amount = 5000;
      } else {
        this.coupon_amount = 0;
      }

      if (this.coupon_amount > 0) {
        let formData = {
          coupon_order: this.placedOrderID,
          coupon_amount: Number(this.coupon_amount).toFixed(2),
          status: this.coupon_status
        };
        this.axios.post("api/v1/generate-coupon/", formData).then(response => {
          console.log("The coupon amount is ", this.coupon_amount);
        }).catch(error => {
          console.log(error.message);
        });
      }
    },

    requestDelivery() {
      this.deliveryOption = !this.deliveryOption;

      if (this.deliveryOption == false) {
        this.town = "";
      }
    },

    showLipaNaMpesa() {
      this.lipaNaMpesa = !this.lipaNaMpesa;
    },

    showShippingDetails() {
      this.checked = !this.checked;
      console.log(this.checked);
    },

    closeModal() {
      this.isModalVisible = false;
    },

    mountPaypalButton() {},

    scrollToTop() {
      window.scrollTo({
        top: 0,
        behavior: "smooth"
      });
    }

  },

  beforeMount() {
    this.getProfileDetails();
    this.cart = this.$store.state.cart;
    this.isAuthenticated = this.$store.state.isAuthenticated;
  },

  mounted() {
    // this.registerCallbackUrl();
    if (!this.isAuthenticated) {
      this.$toast.error("You need to create an account before proceeding to checkout");
    }

    paypal.Buttons({
      //Styling the paypal button
      style: {
        color: "blue",
        shape: "pill",
        label: "pay",
        height: 40
      },
      // Sets up the transaction when a payment button is clicked
      createOrder: (data, actions) => {
        return actions.order.create({
          purchase_units: [{
            amount: {
              // value: this.cartTotalUSD // Can also reference a variable or function
              value: 0.01
            }
          }]
        });
      },
      // Finalize the transaction after payer approval
      onApprove: (data, actions) => {
        const my_action = actions.order.capture().then(function (orderData) {
          // Successful capture! For dev/demo purposes:
          console.log("Capture result", orderData, JSON.stringify(orderData, null, 2));
          this.transaction_id = orderData.purchase_units[0].payments.captures[0].id;
          console.log("the transaction_id is:", this.transaction_id);
          const transaction = orderData.purchase_units[0].payments.captures[0];
          alert(`Transaction ${transaction.status}: ${transaction.id}\n\nSee console for all available details`); // this.placeOrder();
          // When ready to go live, remove the alert and show a success message within this page. For example:
          // const element = document.getElementById('paypal-button-container');
          // element.innerHTML = '<h3>Thank you for your payment!</h3>';
          // Or go to another URL:  actions.redirect('thank_you.html');
        });
        return my_action;
      }
    }).render("#paypal-button-container");
  }

};
