import { createStore } from 'vuex'

export default createStore({
  state: {
    cart:{
      cartItems:[]
    }
  },
  getters: {

  },
  mutations: {
    initializeStore(state){
      //Check if the cart exists in the local storage
      if(localStorage.getItem('cart')){
        state.cart = JSON.parse(localStorage.getItem('cart'))
        // console.log(state.cart.cartItems[0])
      //Add the cart to local storage if it doesn't exist
      }else{
        localStorage.setItem('cart',JSON.stringify(state.cart))
      }
    },
    addToCart(state, cartItem){
      // const exist = [];
      //Check if the item exists in the cart
      // const exists = state.cart.cartItems.filter(i => i.item.id === cartItem.item.id)=== state.cart.cartItems.items.id

      if(state.cart.cartItems.length > 0){
        const exist = state.cart.cartItems.filter((i) => {
          return i.items.id === state.cart.cartItems.items.id;
        })
      }
      else {
        console.log(state.cart.cartItems.length);
      }
      // console.table(exist);
      if(exist.length){
        exist[0].quantity = parseInt(exist[0].quantity) + parseInt(cartItem.quantity)
      }else{
        state.cart.cartItems.push(cartItem)
      }

      // })
      // if(exists.length){
      //   exists[0].quantity = parseInt(exists[0].quantity) + parseInt(cartItem.quantity)
      // }else{
      //   state.cart.cartItems.push(cartItem)
      // }
      //Stores cart information in the local storage
      localStorage.setItem('cart',JSON.stringify(state.cart))
    }
  },
  actions: {
  },
  modules: {
  }
})
