import Modal from "@/components/Modal.vue";
import Pagination from "@/components/Pagination.vue";
import DoughnutChart from "@/components/DoughnutChart.vue";
export default {
  props: ["getUserDetails", "userDetails"],
  components: {
    Modal,
    Pagination,
    DoughnutChart
  },

  data() {
    return {
      id: "",
      first_name: "",
      last_name: "",
      email: "",
      phone_number: "",
      birthdate: "",
      address: "",
      county: "",
      city: "",
      gender: "",
      errors: [],
      myOrders: [],
      myOrderDetails: [],
      isModalVisible: false,
      isPayModalVisible: false,
      bankModalVisible: false,
      paymentModalVisible: false,
      lipaNaMpesa: false,
      currentPage: 1,
      totalItems: 0,
      itemsPerPage: 2,
      totalPages: 0,
      start: 1,
      limit: 2,
      watcherMsg: [],
      fnStyle: null,
      lnStyle: null,
      nStyle: null,
      gStyle: null,
      bWidth: null,
      dStyle: null,
      cStyle: null,
      ccStyle: null,
      aStyle: null,
      pStyle: null,
      p2Style: null,
      rfStyle: null,
      payment_number: "",
      payment_number2: "",
      mpesaRefs: [],
      mpesaRef: "",
      paymentConfirm: false,
      myOrderID: "",
      pageOfItems: [],
      shippingAddress: false,
      currentPassword: "",
      newPassword: "",
      newPassword2: "",
      showPass: false,
      passType: false,
      showPass2: false,
      pass2Type: false,
      orderTotal: 0
    };
  },

  watch: {
    first_name(value) {
      this.first_name = value;
      this.validateFirstName(value);
    },

    last_name(value) {
      this.last_name = value;
      this.validateLastName(value);
    },

    phone_number(value) {
      this.phone_number = value;
      this.validatePhoneNumber(value);
    },

    gender(value) {
      this.gender = value;
      this.validateGender(value);
    },

    birthdate(value) {
      this.birthdate = value;
      this.validateBirthdate(value);
    },

    city(value) {
      this.city = value;
      this.validateCity(value);
    },

    county(value) {
      this.county = value;
      this.validateCounty(value);
    },

    address(value) {
      this.address = value;
      this.validateAddress(value);
    },

    payment_number(value) {
      this.payment_number = value;
      this.validatePaymentNumber(value);
    },

    payment_number2(value) {
      this.payment_number2 = value;
      this.confirmPaymentNumber(value);
    },

    mpesaRef(value) {
      this.mpesaRef = value;
      this.validatePaymentRef(value);
    },

    newPassword2(value) {
      this.newPassword2 = value;
      this.validatePassword2(value);
    },

    newPassword(value) {
      this.newPassword = value;
      this.validateNewPassword(value);
    }

  },
  computed: {
    orderItemTotal() {
      let itemTotal = [];

      for (let i = 0; i < this.myOrderDetails.items.length; i++) {
        let lineTotal = (this.myOrderDetails.items[i].quantity * this.myOrderDetails.items[i].product.price).toFixed(2);
        itemTotal.push(lineTotal);
      }

      return itemTotal;
    }

  },
  methods: {
    validateFirstName(value) {
      if (value.length == 0) {
        this.watcherMsg["first_name"] = "";
        this.fnStyle = "";
        this.bWidth = 1;
      } else {
        if (value.length > 0 && value.length < 3) {
          this.watcherMsg["first_name"] = "Name is too short";
          this.fnStyle = "red";
          this.bWidth = 2;
        } else {
          if (/\d/.test(value)) {
            this.watcherMsg["first_name"] = "Name should not contain a number";
            this.fnStyle = "red";
            this.bWidth = 2;
          } else {
            this.watcherMsg["first_name"] = "";
            this.fnStyle = "green";
            this.bWidth = 2;
          }
        }
      }
    },

    validateLastName(value) {
      if (value.length == 0) {
        this.watcherMsg["last_name"] = "";
        this.lnStyle = "";
        this.bWidth = 1;
      } else {
        if (value.length > 0 && value.length < 3) {
          this.watcherMsg["last_name"] = "Name is too short";
          this.lnStyle = "red";
          this.bWidth = 2;
        } else {
          if (/\d/.test(value)) {
            this.watcherMsg["last_name"] = "Name should not contain a number";
            this.lnStyle = "red";
            this.bWidth = 2;
          } else {
            this.watcherMsg["last_name"] = "";
            this.lnStyle = "green";
            this.bWidth = 2;
          }
        }
      }
    },

    validatePhoneNumber(value) {
      if (value == "") {
        this.nStyle = "";
        this.watcherMsg["phone_number"] = "";
      } else {
        if (/^(?:254|\+254|0)?((?:(?:7(?:(?:[01249][0-9])|(?:5[789])|(?:6[89])))|(?:1(?:[1][0-5])))[0-9]{6})$/.test(value) || /^(?:254|\+254|0)?((?:(?:7(?:(?:3[0-9])|(?:5[0-6])|(8[5-9])))|(?:1(?:[0][0-2])))[0-9]{6})$/.test(value)) {
          this.nStyle = "green";
          this.watcherMsg["phone_number"] = "";
          this.bWidth = 2;
        } else {
          this.nStyle = "red";
          this.watcherMsg["phone_number"] = "Invalid Phone Number";
          this.bWidth = 2;
        }
      }
    },

    validateGender(value) {
      if (value != "") {
        this.gStyle = "green";
        this.bWidth = 2;
      }
    },

    validateBirthdate(value) {
      if (value != "") {
        this.bStyle = "green";
        this.bWidth = 2;
      }
    },

    validateCity(value) {
      if (value != "") {
        this.cStyle = "green";
        this.bWidth = 2;
      }
    },

    validateCounty(value) {
      if (value != "") {
        this.ccStyle = "green";
        this.bWidth = 2;
      }
    },

    validateAddress(value) {
      if (value.length == 0) {
        this.aStyle = "";
        this.watcherMsg["address"] = "";
      } else {
        if (value.length > 0 && value.length < 3) {
          this.watcherMsg["address"] = "Address is too short";
          this.aStyle = "red";
          this.bWidth = 2;
        } else {
          if (value != "" && value.length > 3) {
            this.aStyle = "green";
            this.bWidth = 2;
            this.watcherMsg["address"] = "";
          }
        }
      }
    },

    validatePaymentNumber(value) {
      if (value == "") {
        this.watcherMsg["payment_number"] = "";
        this.pStyle = "";
        this.bWidth = 1;
      } else {
        if (/^(?:254)?((?:(?:7(?:(?:[01249][0-9])|(?:5[789])|(?:6[89])))|(?:1(?:[1][0-5])))[0-9]{6})$/.test(value)) {
          this.watcherMsg["payment_number"] = "";
          this.pStyle = "green";
          this.bWidth = 2;
        } else {
          this.watcherMsg["payment_number"] = "Invalid Phone Number";
          this.pStyle = "red";
          this.bWidth = 2;
        }
      }
    },

    confirmPaymentNumber(value) {
      if (value == "") {
        this.watcherMsg["payment_number2"] = "";
        this.p2Style = "";
        this.bWidth = 1;
      } else {
        if (value == this.payment_number) {
          this.watcherMsg["payment_number2"] = "";
          this.p2Style = "green";
          this.bWidth = 2;
        } else {
          this.watcherMsg["payment_number2"] = "Invalid Phone Number";
          this.p2Style = "red";
          this.bWidth = 2;
        }
      }
    },

    validatePaymentRef(value) {
      if (value.length == 0) {
        this.watcherMsg["mpesaRef"] = "";
        this.rfStyle = "";
        this.bWidth = 1;
      } else {
        if (value.length > 0 && value.length < 10) {
          this.rfStyle = "red";
          this.watcherMsg["mpesaRef"] = "Incomplete Reference Number";
          this.bWidth = 2;
        } else {
          if (/^[A-Z0-9]$/.test(value)) {
            this.rfStyle = "green";
            this.watcherMsg["mpesaRef"] = "";
            this.bWidth = 2;
          }
        }
      }
    },

    validateNewPassword(value) {
      if (value.length == "") {
        this.watcherMsg["newPassword"] = "";
      } else {
        if (value.length > 0 && value.length < 8) {
          this.watcherMsg["newPassword"] = "Password is too short";
        } else {
          this.watcherMsg["newPassword"] = "";
        }
      }
    },

    validatePassword2(value) {
      if (value == this.newPassword) {
        this.watcherMsg["newPassword2"] = "";
      } else {
        this.watcherMsg["newPassword2"] = "Passwords do not match";
      }
    },

    onChangePage(pageOfItems) {
      // update page of items
      this.pageOfItems = pageOfItems;
    },

    postProfileData() {
      this.errors = [];

      if (this.first_name === "" && this.last_name === "" && this.birthdate === "" && this.gender === "" && this.city === "" && this.county === "" && this.address === "") {
        this.errors.push("Please fill in the details!");
      } else {
        if (this.first_name === "" || this.last_name === "" || this.birthdate === "" || this.gender === "" || this.city === "" || this.county === "" || this.address === "") {
          this.errors.push("Some details are missing!");
        }
      }

      if (!this.errors.length) {
        let formData = {
          first_name: this.first_name,
          last_name: this.last_name,
          birthdate: this.birthdate,
          address: this.address,
          county: this.county,
          city: this.city,
          gender: this.gender,
          phone_number: this.phone_number,
          email: this.email
        };
        this.axios.put("/api/v1/user-list/" + this.id + "/", formData).then(response => {
          this.$toast.success("Profile Succesfully Updated", {
            duration: 5000,
            dismissible: true
          });
          this.fnStyle = "";
          this.lnStyle = "";
          this.gStyle = "";
          this.cStyle = "";
          this.ccStyle = "";
          this.aStyle = "";
          this.$router.push("/my-account");
        }).catch(error => {
          console.log(error);
        }).finally(() => {
          this.fnStyle = null, this.lnStyle = null;
          this.nStyle = null;
          this.gStyle = null;
          this.bWidth = null;
          this.dStyle = null;
          this.cStyle = null;
          this.ccStyle = null;
          this.aStyle = null;
          this.pStyle = null;
          this.p2Style = null;
        });
      }
    },

    changePassword() {
      let formData = {
        password: this.newPassword
      };
      this.axios.patch("/api/v1/users/me/", formData).then(response => {
        console.log(response.data);
        this.$toast.success("Password Changed Succesfully");
        this.newPassword = "";
        this.newPassword2 = "";
        this.curreentPassword = "";
        this.$store.commit("removeToken");
        const token = this.$store.state.token;
        localStorage.setItem("token", token);
        this.$router.push("/login");
      }).catch(error => {
        console.log(error);
      });
    },

    getProfileDetails() {
      this.axios.get("/api/v1/users/me/").then(response => {
        this.phone_number = response.data.phone_number;
        this.email = response.data.email;
        this.id = response.data.id;
        this.first_name = response.data.first_name;
        this.last_name = response.data.last_name;
        this.gender = response.data.gender;
        this.city = response.data.city;
        this.county = response.data.county;
        this.birthdate = response.data.birthdate;
        this.address = response.data.address;
        this.currentPassword = response.data.password;
      }).catch(error => {
        console.log(error);
      }).finally(() => {
        this.fnStyle = null, this.lnStyle = null;
        this.nStyle = null;
        this.gStyle = null;
        this.bWidth = null;
        this.dStyle = null;
        this.cStyle = null;
        this.ccStyle = null;
        this.aStyle = null;
        this.pStyle = null;
        this.p2Style = null;
      });
    },

    getMpesaPayments() {
      this.mpesaRefs = [];
      this.axios.get("api/v1/mpesa-payments/").then(response => {
        for (let i = 0; i < response.data.length; i++) {
          let trans_id = response.data[i].transaction_id;
          this.mpesaRefs.push(trans_id);
        }

        this.isPayModalVisible = true;
      }).catch(error => {
        console.log(error);
      });
    },

    confirmPayment() {
      if (this.mpesaRefs.includes(this.mpesaRef)) {
        let formData = {
          paid: true,
          payment_reference: this.mpesaRef
        };
        this.axios.patch(`/api/v1/my-orders/${this.myOrderID}/`, formData).then(response => {
          this.$toast.success("Payment Confirmation Succesful", {
            duration: 3000
          });
          this.mpesaRef = "";
          this.payment_number2 = "";
          this.$router.push("/my-account");
          this.$store.commit("reloadingPage");
        }).catch(error => {
          console.log(error);
        });
      } else {
        this.$toast.error("Invalid Reference Number", {
          duration: 3000
        });
      }
    },

    showClientOrders() {
      this.axios.get("api/v1/my-orders/").then(response => {
        this.myOrders = response.data;
      }).catch(error => {
        console.log(error);
      });
    },

    showModal() {
      this.isModalVisible = true;
      let orderID = arguments[0];
      this.axios.get(`/api/v1/my-orders/${orderID}/`).then(response => {
        this.myOrderDetails = response.data;
      }).catch(error => {
        console.log(error);
      });
    },

    showPaymentModal() {
      this.paymentModalVisible = true;
      let orderID = arguments[0];
      this.axios.get(`/api/v1/my-orders/${orderID}/`).then(response => {
        this.myOrderID = orderID;
        console.log(response.data);
        this.orderTotal = Math.round(response.data.order_total);
        console.log(this.orderTotal);
      }).catch(error => {
        console.log(error);
      });
    },

    showBankModal() {
      this.bankModalVisible = true;
    },

    closeModal() {
      this.isModalVisible = false;
      this.paymentModalVisible = false;
      this.bankModalVisible = false;
    },

    showLipaNaMpesa() {
      this.lipaNaMpesa = !this.lipaNaMpesa;
    },

    showShippingAddress() {
      this.shippingAddress = true;
    },

    payViaMpesa() {
      this.paymentConfirm = true;
      this.errors = [];

      if (this.payment_number === "") {
        this.errors.push("Please enter phone number");
        this.$toast.error("Invalid phone number", {
          duration: 3000
        });
      }

      if (!this.errors.length && this.pStyle == "green") {
        let formData = {
          payment_number: this.payment_number,
          first_name: this.first_name,
          orderTotal: this.orderTotal
        };
        this.axios.post("/api/v1/online/lipa/", formData).then(response => {
          console.log(response.data);
          this.paymentConfirm = true;
        }).catch(error => {
          console.log(error);
        }).finally(() => {});
      }
    },

    showPassword() {
      if (!this.showPass) {
        this.showPass = true;
        this.passType = true;
      } else {
        this.showPass = false;
        this.passType = false;
      }
    },

    showPassword2() {
      if (!this.showPass2) {
        this.showPass2 = true;
        this.pass2Type = true;
      } else {
        this.showPass2 = false;
        this.pass2Type = false;
      }
    },

    printOrder() {
      let orderID = arguments[0];
      this.axios.get(`api/v1/view_pdf/${orderID}/`).then(response => {
        console.log(response);
      }).catch(error => {
        console.log(error);
      });
    }

  },

  beforeMount() {},

